const express = require('express')();
express.get('http://youtube.com')
export default function dbconnect(){
    const mysql = require('mysql');
const connection = mysql.createConnection({
  host: 'localhost',
  user: 'root',
  password: '1234',
  database: 'todolist'
});

connection.connect((err) => {
  if (err) throw err;
  alert('Connected!');
});
}
