import { useContext } from "react";
import Todoitem from "./Todoitem.js";
import { UContext } from "../index.js";

export default function Todotable({ items, handledone, setpopup }) {
  const  [ongoingstyle, setongoingstyle] = useContext(UContext)
  return (

    <div
      className="todobox"
      style={{ justifyContent: items.length !== 0 ? "start" : "center", }}
    >
      {items.map((item, index) => {
        return (
          <Todoitem
            items={item}
            handledone={() => handledone(index)}
            key={index}
          />
        );
      })}
      <h1
        className="boxHeader"
      >
        Current Tasks
      </h1>
      {items.length !== 0 ? "" : <img alt="empty" className="Hidden" src="\flat-icon-trash-can-flies-260nw-2127192293.png" style={{width: '100px'}}/>}
    </div>
  );
}
