import axios from "axios"
import { useContext, useState } from "react"
import { useNavigate } from "react-router-dom"
import {UContext} from '../index.js'
export default function NavBar({setpopup,username: currentuser}){
    const [login_out, setlogin_out] = useState('logout')
    const  {ongoingstyle, setongoingstyle,   expiredstyle, setonexpired,setcompletedstyle,completedstyle} = useContext(UContext)



    const navigate = useNavigate()
    
    async function handlelogout(){
       if( window.confirm("You sure nigga?")){
        setlogin_out('Loading...')
        await axios.post('/logout',{},{withCredentials: true})
        navigate(0)
    }
    }
   
    var defaults ={
    
        height: '50%',
        width: '70%'
    }
    return(
        <header>
            <h1>TodoTheList</h1>
            <ul>
                <li>Expired</li>
                <li onClick={() =>{ 
                setInterval(() => {
                    setongoingstyle({width: '250%'}) 
                }, 500);
                setonexpired({...defaults, transform: 'translate(calc(20%), 100%) rotate(90deg)', boxShadow: 'none'})
                setcompletedstyle({...defaults, transform: 'rotate(90deg)'})

              
     
            
            }} >Ongoing tasks</li>
                <li>Done</li>
                <li style={{backgroundColor:"green"}} onClick={() => setpopup(true)}>Add item</li>
            </ul>
            <span className="Sign-Welcome">
                {currentuser?
            <p>What's on your mind today, <span>{String(currentuser).charAt(0).toUpperCase() + String(currentuser).slice(1)}</span>
            </p>: 'Loading'}
        </span>
    <button className="logout" onClick={handlelogout}>{login_out}</button>
        </header>
    )
}
