import { useState } from 'react';
import './App.css';

function Todotable({ items }) {
  return (
    <div className='todobox'>
      {console.log(items)}

      {items.map((item, index) => {
        return <Todoitem key={index} items={item} />; // Add a key prop
      })}
      <button className='addbuttontodobox'>Add new Item</button>
    </div>
  );
}

function Todoitem({ items }) {
  return (
    <div className='todoitem'>
      <div className='leftofitem'>
        <div className='top'>{items.Name}</div> {/* Change to items.Name */}
        <div className='description'>{items.desc}</div>
        <div className='end'>created at {items.time}</div>
      </div>
      <div className='rightofitem'>
        <button>Done</button>
      </div>
    </div>
  );
}

function Additemform({ Name, desc, time, setname, setdesc, settime, handleadd }) {
  return (
    <div className="addingform">
      <input value={Name} onChange={(e) => setname(e.target.value)} placeholder='Item name' />
      <textarea value={desc} onChange={(e) => setdesc(e.target.value)} placeholder='description'></textarea>
      <input type='date' value={time} onChange={(e) => settime(e.target.value)} />
      <button onClick={handleadd}>Add</button>
    </div>
  );
}

function App() {
  const [Name, setname] = useState('');
  const [desc, setdesc] = useState('');
  const [time, settime] = useState('');
  const [items, setitems] = useState([]);

  function handleadd() {
    const newitem = { Name, desc, time }; // Use correct property names
    setitems([...items, newitem]);
    setname(''); // Clear input
    setdesc(''); // Clear input
    settime(''); // Clear input
  }

  return (
    <div className="App-header">
      <Todotable items={items} />
      <Additemform
        Name={Name}
        desc={desc}
        time={time}
        setdesc={setdesc}
        setname={setname}
        settime={settime}
        handleadd={handleadd}
      />
    </div>
  );
}

export default App;
