import { useEffect, useRef, useState } from "react";
import "./App.css";

function Todolist({ items, handleToggleDone, handleDelete, refobject }) {
  const inputref = refobject.inputref;

  function focus() {
    inputref.current.focus();
    refobject.addingform.current.style.backgroundColor = "blue";
    inputref.current.style.color = "black";
    refobject.inputref.current.style.border = "2px solid red";
  }

  return (
    <div
      className="todobox"
      style={{ justifyContent: items.length !== 0 ? "start" : "center" }}
    >
      {items.map((item, index) => {
        return (
          <div key={index} className={`todoitem ${item.completed ? "completed" : ""}`}>
            <div className="leftofitem">
              <div className="top">
                {item.Name.length > 20 ? item.Name.slice(0, 20) + "..." : item.Name}{" "}
                {item.currentdate}
              </div>
              <hr></hr>
              <div className="description">
                {item.desc.length > 50 ? item.desc.slice(0, 50) + "..." : item.desc}
              </div>
              <hr></hr>
              <div className="end">Due {item.time.length > 10 ? item.time.slice(0, 10) + "..." : item.time}</div>
            </div>
            <div className="rightofitem">
              {item.completed ? (
                <button onClick={() => handleDelete(index)}>Delete</button>
              ) : (
                <button onClick={() => handleToggleDone(index)}>Done</button>
              )}
            </div>
          </div>
        );
      })}
      <button
        className="addbuttontodobox"
        onClick={focus}
        style={{
          position: items.length !== 0 ? "static" : "absolute",
          top: 15,
        }}
      >
        Add new Item
      </button>
      {items.length === 0 && <p className="Hidden">No Tasks</p>}
    </div>
  );
}

function Additemform({ refobject, Name, desc, time, setname, setdesc, settime, handleadd }) {
  function changecolor() {
    refobject.addingform.current.style.backgroundColor = "white";
  }

  return (
    <form
      ref={refobject.addingform}
      className="addingform"
      onSubmit={(e) => {
        e.preventDefault();
        handleadd();
      }}
    >
      <input
        id="namein"
        ref={refobject.inputref}
        value={Name}
        onChange={(e) => {
          setname(e.target.value);
          changecolor();
        }}
        placeholder="Item name"
        required
      />
      <textarea
        value={desc}
        onChange={(e) => setdesc(e.target.value)}
        placeholder="description"
      />
      <input
        type="date"
        min={new Date().toISOString().slice(0, 10)}
        value={time}
        onChange={(e) => settime(e.target.value)}
        required
      />
      <button>Add</button>
    </form>
  );
}

function App() {
  const addingform = useRef();
  const inputref = useRef();
  const refobject = { inputref, addingform };

  const storedItems = localStorage.getItem("items");
  const [Name, setname] = useState("");
  const [desc, setdesc] = useState("");
  const [time, settime] = useState("");
  const [items, setitems] = useState(storedItems ? JSON.parse(storedItems) : []);

  useEffect(() => localStorage.setItem("items", JSON.stringify(items)), [items]);

  function handleToggleDone(index) {
    const newItems = [...items];
    newItems[index].completed = true;
    newItems[index].currentdate = new Date().toJSON().slice(0, 10);
    setitems(newItems);
  }

  function handleDelete(index) {
    const newItems = items.filter((_, i) => i !== index);
    setitems(newItems);
  }

  function handleadd() {
    const currentdate = new Date().toJSON().slice(0, 10);
    const newitem = { Name, desc: desc || "No Description", time, currentdate, completed: false };
    setitems([...items, newitem]);
    setname("");
    setdesc("");
    settime("");
  }

  return (
    <div className="App-header">
      <Todolist
        items={items}
        handleToggleDone={handleToggleDone}
        handleDelete={handleDelete}
        refobject={refobject}
      />
      <Additemform
        refobject={refobject}
        Name={Name}
        desc={desc}
        time={time}
        setdesc={setdesc}
        setname={setname}
        settime={settime}
        handleadd={handleadd}
      />
    </div>
  );
}

export default App;
