import { Fragment, useEffect, useRef, useState } from "react";
import "./App.css";

function Todotable({ items, handledone, refobject }) {
  let inputref = refobject.inputref;
  function focus() {
    inputref.current.focus();
    refobject.addingform.current.style.backgroundColor = "blue";
    inputref.current.style.color = "black";
    refobject.inputref.current.style.border = "2px solid red";
  }

  return (
    <div
      className="todobox"
      style={{ justifyContent: items.length !== 0 ? "start" : "center" }}
    >
      {items.map((item, index) => {
        return (
          <Todoitem
            items={item}
            handledone={() => handledone(index)}
            key={index}
          />
        );
      })}
      <button
        className="addbuttontodobox"
        onClick={focus}
        style={{
          position: items.length !== 0 ? "static" : "absolute",
          top: 15,
        }}
      >
        Add new Item
      </button>
      {items.length !== 0 ? "" : <p className="Hidden">No Tasks</p>}
    </div>
  );
}

function Todoitem({ items, handledone }) {
  return (
    <div className="todoitem">
      <div className="leftofitem">
        <div className="top">
          {items.Name.length > 20
            ? items.Name.slice(0, 20) + "..."
            : items.Name}{" "}
          {items.currentdate}
        </div>
        <hr></hr>
        <div className="description">
          {items.desc.length > 50
            ? items.desc.slice(0, 50) + "..."
            : items.desc}
        </div>{" "}
        <hr></hr>
        <div className="end">
          Due{" "}
          {items.time.length > 10
            ? items.time.slice(0, 10) + "..."
            : items.time}
        </div>
      </div>
      <div className="rightofitem">
        <button onClick={handledone}>Done</button>
      </div>
    </div>
  );
}

function Additemform({
  refobject,
  Name,
  desc,
  time,
  setname,
  setdesc,
  settime,
  handleadd,
}) {
  function changecolor() {
    refobject.addingform.current.style.backgroundColor = "white";
  }
  return (
    <form
      ref={refobject.addingform}
      className="addingform"
      onSubmit={(e) => {
        e.preventDefault();
        handleadd();
      }}
    >
      <input
        id="namein"
        ref={refobject.inputref}
        value={Name}
        onChange={(e) => {
          setname(e.target.value);
          changecolor();
        }}
        placeholder="Item name"
        required
      />
      <textarea
        value={desc}
        onChange={(e) => setdesc(e.target.value)}
        placeholder="description"
      />
      <input
        type="date"
        min={new Date().toISOString().slice(0, 10)}
        value={time}
        onChange={(e) => settime(e.target.value)}
        required
      />
      <button>Add</button>
    </form>
  );
}

function Completed({ completedItems, handledelete }) {
  return (
    <div
      className="completedbox todobox"
      style={{
        justifyContent: completedItems.length !== 0 ? "start" : "center",
      }}
    >
      {completedItems.map((item, index) => (
        <div key={index} className="todoitem completed">
          <div className="leftofitem">
            <div className="top">
              {item.Name.length > 20
                ? item.Name.slice(0, 20) + "..."
                : item.Name}{" "}
              {item.currentdate}
            </div>
            <hr></hr>
            <div className="description">
              {item.desc.length > 50
                ? item.desc.slice(0, 50) + "..."
                : item.desc}
            </div>
            <hr></hr>
            <div className="end" style={{textWrap: now}}>
              Completed at {true && new Date().toJSON().slice(0,10)}
            </div>
          </div>
          <div className="rightofitem">
            <button onClick={() => handledelete(index)}>Delete</button>
          </div>
        </div>
      ))}

      {completedItems.length !== 0 ? "" : <p className="Hidden">No Tasks</p>}
    </div>
  );
}

function App() {
  const addingform = useRef();
  const inputref = useRef();
  const refobject = { inputref, addingform };
  const storeditem = localStorage.getItem("items");
  const storedcompleted = localStorage.getItem("citems");
  const [Name, setname] = useState("");
  const [desc, setdesc] = useState("");
  const [time, settime] = useState("");
  const [items, setitems] = useState(storeditem ? JSON.parse(storeditem) : []);
  const [completedItems, setCompletedItems] = useState(
    storedcompleted ? JSON.parse(storedcompleted) : []
  );
  useEffect(
    () => localStorage.setItem("items", JSON.stringify(items)),
    [items]
  );
  useEffect(
    () => localStorage.setItem("citems", JSON.stringify(completedItems)),
    [completedItems]
  );
  function handledone(index) {
    // Move the completed item to the completedItems array
    let completedItem = items[index];
    completedItem.currentdate = new Date().toJSON().slice(0, 10);

    setCompletedItems([...completedItems, completedItem]);

    // Remove the completed item from the items array
    setitems(items.filter((_, i) => i !== index));
  }

  function handledelete(index) {
    const temp = completedItems.filter((item, i) => i !== index);

    setCompletedItems(temp);
  }

  function handleadd() {
    const currentdate = new Date().toJSON().slice(0, 10);

    // Trim and validate inputs
    let trimmedDesc = desc.trim();
    if (trimmedDesc === "") {
        trimmedDesc = ("No Description");
    }

    if (Name.trim() === "" || time.trim() === "") {
        alert("Please fill in all fields.");
        return; // Exit if validation fails
    }

    const newItem = { Name: Name.trim(), desc: trimmedDesc, time: time.trim(), currentdate };
    refobject.inputref.current.style.border = "1px solid #ccc"; // Corrected border syntax
    setitems(items => [...items, newItem]);

    // Clear input fields
    setname("");
    setdesc("");
    settime("");
}


  return (
    <div className="App-header">
      <Completed completedItems={completedItems} handledelete={handledelete} />
      <Todotable items={items} handledone={handledone} refobject={refobject} />
      <Additemform
        refobject={refobject}
        Name={Name}
        desc={desc}
        time={time}
        setdesc={setdesc}
        setname={setname}
        settime={settime}
        handleadd={handleadd}
      />
    </div>
  );
}

export default App;
