import React, { useState } from 'react';
import './App.css';

function TodoTable({ addItem }) {
  return (
    <div className='todobox'>
      <TodoItem />
      <TodoItem />
      <button className='addbuttontodobox' onClick={addItem}>
        Add new Item
      </button>
    </div>
  );
}

function TodoItem() {
  return (
    <div className='todoitem'>
      <div className='leftofitem'>
        <div className='top'>name due 10pm</div>
        <div className='description'>description</div>
        <div className='end'>created at 10am</div>
      </div>
      <div className='rightofitem'>
        <button>Done</button>
      </div>
    </div>
  );
}

function AddItemForm({ handleAdd }) {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Send the input values to handleAdd
    handleAdd({ name, description });
    // Clear input fields after submission
    setName('');
    setDescription('');
  };

  return (
    <div className='addingform'>
      <form onSubmit={handleSubmit}>
        <input
          name='name'
          placeholder='Item name'
          value={name}
          onChange={(e) => setName(e.target.value)} // Update state on input change
        />
        <textarea
          placeholder='description'
          value={description}
          onChange={(e) => setDescription(e.target.value)} // Update state on textarea change
        ></textarea>
        <button type='submit'>Add</button>
      </form>
    </div>
  );
}

function App() {
  const [tasks, setTasks] = useState([]);

  const handleAdd = (newTask) => {
    setTasks([...tasks, { ...newTask, createdAt: new Date().toLocaleTimeString() }]);
  };

  return (
    <div className='App-header'>
      <TodoTable />
      <AddItemForm handleAdd={handleAdd} />
      {/* Display tasks */}
      <div className="task-list">
        {tasks.map((task, index) => (
          <div key={index} className='task-item'>
            <h3>{task.name}</h3>
            <p>{task.description}</p>
            <small>Created at: {task.createdAt}</small>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
