import React, { useContext, useEffect } from "react";
import { UContext } from "../index.js";

export default function Expired({ handledeleteex, expiredItems }){
  const { expiredstyle, setonexpired,ongoingstyle, completedstyle } = useContext(UContext);

  return (
    <div
      className="expiredbox todobox"
      style={{
        justifyContent: expiredItems.length !== 0 ? "start" : "center",
        ...expiredstyle,
      }}
    >
      <h1 className="boxHeader"
      style={ongoingstyle.flex == 3 ? {textWrap: "nowrap", transform: 'rotate(90deg)', top:'calc(50% - 25px)'}: {top: '-10px'}}
      style={completedstyle.flex == 3? {textWrap: "nowrap", transform: 'rotate(90deg)', top:'calc(50% - 25px)'}: {top: '-10px'}}
      >Expired Tasks</h1>
      {!expression && expiredItems.map((item, index) => (
        <div key={index} className="todoitem completed">
          <div className="leftofitem">
            <div className="top">
              {item.Name.length > 20
                ? item.Name.slice(0, 20) + "..."
                : item.Name}{" "}
              {item.currentdate}
            </div>
            <hr></hr>
            <div className="description" style={{ textWrap: "pretty" }}>
              {item.desc.length > 50
                ? item.desc.slice(0, 50) + "..."
                : item.desc}
            </div>
            <hr></hr>
            <div className="end">
              Completed at {true && new Date().toJSON().slice(0, 10)}
            </div>
          </div>
          <div className="rightofitem">
            <button onClick={() => handledeleteex(index)}>Delete</button>
          </div>
        </div>
      ))}

      {expiredItems.length === 0 ? (
        !expression ? (
          <img
            alt="empty"
            className="Hidden"
            src="\flat-icon-trash-can-flies-260nw-2127192293.png"
            style={{ width: "100px" }}
          />
        ) : null
      ) : null}
    </div>
  );
}
