import axios from "axios";
import { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { UContext } from "../index.js";
export default function NavBar({ setpopup, username: currentuser }) {
  const [login_out, setlogin_out] = useState("logout");
  const {
    guest,
    turnoff,
    setturnoff,
    ongoingstyle,
    setongoingstyle,
    epiredstyle,
    setonexpired,
    setcompletedstyle,
    completedstyle,
  } = useContext(UContext);
  const [sentw, setsentw] = useState();
  useEffect(() => {
    if (guest) {
      setlogin_out("Login");
    } else {
      setlogin_out("Log out");
    }
  }, [guest]);
  useEffect(() => {
    if (turnoff) {
      setsentw("Turn Box reactivity off");
      setongoingstyle({});
      setonexpired({});
      setcompletedstyle({});
    } else if (!turnoff) {
      setsentw("Turn Box reactivity on");
      setongoingstyle({});
      setonexpired({});
      setcompletedstyle({});
    }
  }, [turnoff]);
  var expanded = {
    flex: "3",
    flexWrap: "wrap",
    overflowX: "auto",
    overflowY: "hidden",
  };
  const navigate = useNavigate();

  async function handlelogout() {
    if (login_out == "Login") {
      navigate("/Sign");
      navigate(0);
    } else {
      if (window.confirm("You sure?")) {
        setlogin_out("Loading...");
        await axios.post("/logout", {}, { withCredentials: true });
        navigate(0);
      }
    }
  }

  var defaults = {
    flex: "0",
    overflow: "hidden",
  };
  return (
    <header>
      <h1
        onClick={() => {
          setongoingstyle({});
          setonexpired({});
          setcompletedstyle({});
        }}
      >
        TodoTheList
      </h1>
      <ul>
        <li
          onClick={() => {
            setongoingstyle({ ...expanded });
            setonexpired({ ...defaults });
            setcompletedstyle({ ...defaults });
          }}
        >
          Ongoing tasks
        </li>
        <li
          onClick={() => {
            setongoingstyle({ ...defaults });
            setonexpired({ ...expanded });
            setcompletedstyle({ ...defaults });
          }}
        >
          Expired
        </li>
        <li
          onClick={() => {
            setongoingstyle({ ...defaults });
            setonexpired({ ...defaults });
            setcompletedstyle({ ...expanded });
          }}
        >
          Done
        </li>
        <li
          style={{ width: "150px", textWrap: "wrap" }}
          onClick={() => setturnoff(!turnoff)}
        >
          {sentw}
        </li>
        <li style={{ backgroundColor: "green" }} onClick={() => setpopup(true)}>
          Add item
        </li>
      </ul>
      <span className="Sign-Welcome">
        {currentuser ? (
          <p>
            What's on your mind today,{" "}
            <span>
              {String(currentuser).charAt(0).toUpperCase() +
                String(currentuser).slice(1)}
            </span>
          </p>
        ) : (
          "Loading"
        )}
      </span>
      <button className="logout" onClick={handlelogout}>
        {login_out}
      </button>
    </header>
  );
}
