import React, { useState, createContext } from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
import App from "./App.js";
import Form from "./routes/Form.js";
import {
  createBrowserRouter,
  RouterProvider,
} from "react-router-dom";
import ErrorPage from "./routes/errorpage.js";

// Create the context
export const UContext = createContext();

 function Root() {
  // Move useState hooks inside the functional component
  const [ongoingstyle, setongoingstyle] = useState({hi: 'hu'});
  const [epiredstyle, setonexpired] = useState({hi: 'hu'});
  const [completedstyle, setcompletedstyle] = useState({});

  const router = createBrowserRouter([
    {
      path: "/",
      element: <App />,
      errorElement: <ErrorPage />
    },
    {
      path: "/Sign",
      element: <Form />
    }
  ]);

  return (
    <UContext.Provider value={[ongoingstyle, setongoingstyle, epiredstyle, setonexpired,
    completedstyle, setcompletedstyle]}>
      <RouterProvider router={router} />
    </UContext.Provider>
  );
};

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <Root />
  </React.StrictMode>
);
