  <Todoitem></Todoitem>

  <button className='addbuttontodobox'>Add new Item</button>
</div>
)
}
function Todoitem(){

return(
  <>
  <div className='todoitem'>
  <div className='leftofitem'>
  <div className='top'>name  due 10pm</div>
    <div className='description'>description</div>
    <div className='end'>created at 10am</div>
  </div>
  <div className='rightofitem'>
    <button>Done</button>
  </div>
</div>
</>
)
}
function Additemform({handleadd}){
  return(
    <div className='addingform'>
    <Additembox/>
    </div>
  )
}
function Additembox(){
  const [Name,setname] = useState('')
  const [desc,setdesc] = useState('')
