    <Fragment>
    <input onChange={(e) => setname(e.target.value)}name='name' placeholder='Item name'></input>
    <textarea placeholder='description'></textarea>
    <button>Add</button>
  </Fragment>
  )
}
function App() {
  return (
    <div className="App-header">
      <Todotable/>
      <Additemform/>
    </div>
  );
}

export default App;
