import { Fragment, useEffect, useRef, useState } from 'react';
import './App.css';

function Todotable({ items, handledone, inputref}) {

  function focus(){
    inputref.current.focus()
    inputref.current.style.color = 'red'
  }
  return (
    <div className='todobox' style={{ justifyContent: items.length !== 0 ? 'start' : 'center' }}>

      {items.map((item, index) => {
        return (
          <Todoitem items={item} handledone={() => handledone(index)} key={index} />
        );
      })}
      <button className='addbuttontodobox'  onClick={focus} style={{position: items.length !== 0 ? 'static': 'absolute', top: 15}}>Add new Item</button>
      {items.length !== 0? '':<p className='Hidden'>No Tasks</p>}
    </div>
  );
}

function Todoitem({ items, handledone }) {
  return (
    <div className='todoitem'>
      <div className='leftofitem'>
        <div className='top'>{items.Name + " " + items.currentdate}</div>
        <div className='description'>{items.desc}</div>
        <div className='end'>Due {items.time}</div>
      </div>
      <div className='rightofitem'>
        <button onClick={handledone}>Done</button>
      </div>
    </div>
  );
}

function Additemform({ inputref, Name, desc, time, setname, setdesc, settime, handleadd }) {

  return (
    <form 
    className="addingform" 
    onSubmit={(e) => { e.preventDefault(); handleadd(); }}
    >
      <input
      id='namein'
      ref={inputref}
       value={Name} 
       onChange={(e) => {setname(e.target.value); changecolor}
       }
        placeholder='Item name' required />
      <textarea 
      value={desc} 
      onChange={(e) => setdesc(e.target.value)} 
      placeholder='description' />
      <input 
      type='date'  
      min={new Date().toISOString().slice(0, 10)}
      value={time}
       onChange={(e) => settime(e.target.value)} required />
      <button>Add</button>
    </form>
  );
}

function Completed({ completedItems,handledelete }) {
  return (
    <div className='completedbox todobox' style={{ justifyContent: completedItems.length !== 0 ? 'start' : 'center' }}>
      {completedItems.map((item, index) => (
        <div key={index} className='todoitem completed'>
          <div className='leftofitem'>
            <div className='top'>{item.Name}</div>
            <div className='description'>{item.desc}</div>
            <div className='end'>finished at {item.currentdate}</div>
          </div>
          <div className='rightofitem'>
          <button onClick={() => handledelete(index)}>Delete</button>
          </div>
        </div>
      ))}

     {completedItems.length !== 0? '':<p className='Hidden'>No Tasks</p>}
    </div>
  );
}

function App() {
  const inputref = useRef()
  const storeditem = localStorage.getItem('items')
  const storedcompleted = localStorage.getItem('citems')
  const [Name, setname] = useState('');
  const [desc, setdesc] = useState('');
  const [time, settime] = useState('');
  const [items, setitems] = useState(storeditem? JSON.parse(storeditem) : []);
  const [completedItems, setCompletedItems] = useState(storedcompleted? JSON.parse(storedcompleted) : []);
  useEffect(() => localStorage.setItem('items',JSON.stringify(items)),[items])
  useEffect(() => localStorage.setItem('citems',JSON.stringify(completedItems)),[completedItems])
  function handledone(index) {
    // Move the completed item to the completedItems array
    let completedItem = items[index];
    completedItem.currentdate =  new Date().toJSON().slice(0,10)

    setCompletedItems([...completedItems, completedItem]);

    // Remove the completed item from the items array
    setitems(items.filter((_, i) => i !== index));
  }

  function handledelete(index){
    const temp = completedItems.filter((item, i) => i !== index)

    setCompletedItems(temp)
  }

  function handleadd() {
    const currentdate =  new Date().toJSON().slice(0,10)

    const newitem = { Name, desc, time, currentdate };
    setitems([...items, newitem]);
    setname('');
    setdesc('');
    settime('');
  }

  return (
    <div className="App-header">
      <Completed completedItems={completedItems} handledelete={handledelete} />
      <Todotable items={items} handledone={handledone} inputref={inputref}/>
      <Additemform inputref={inputref} Name={Name} desc={desc} time={time}  setdesc={setdesc} setname={setname} settime={settime} handleadd={handleadd} />
    </div>
  );
}

export default App;
