import { useEffect, useState } from "react"

export default function Todoitem({mode, items, handledone }) {

  const [actionbut,setactionbut] = useState()
  const [divclass,setdivclass] = useState()
  var duetime = (items.time.length > 10
              ? items.time.slice(0, 10) + "..."
              : items.time)
  useEffect(()=>{
    if(mode == 'completed-task'){
      setdivclass('todoitem completed')
      setactionbut('Delete')
    }
    else if(mode == 'expired-task'){
      setdivclass('expiredbox todobox')
      setactionbut('Delete')}
    else{
      setdivclass('todoitem')
    setactionbut('Done')}
  },[])
  
    return (
      <div className={divclass}>
        <div className="leftofitem">
          <div className="top">
            {items.Name.length > 20
              ? items.Name.slice(0, 20) + "..."
              : items.Name}{" "}
            {items.currentdate}
          </div>
          <hr></hr>
          <div className="description" style={{ textWrap: "pretty" }}>
            {items.desc.length > 50
              ? items.desc.slice(0, 50) + "..."
              : items.desc}
          </div>{" "}
          <hr></hr>
          <div className="end">
              {duetime}
          </div>
        </div>
        <div className="rightofitem">
          <button onClick={handledone}>{actionbut}</button>
        </div>
      </div>
    );
  }
