import axios from "axios"
import { useContext, useState } from "react"
import { useNavigate } from "react-router-dom"
import {UContext} from '../index.js'
export default function NavBar({setpopup,username: currentuser}){
    const [login_out, setlogin_out] = useState('logout')
    const  {ongoingstyle, setongoingstyle,   epiredstyle, setonexpired,setcompletedstyle,completedstyle} = useContext(UContext)



    const navigate = useNavigate()
    
    async function handlelogout(){
       if( window.confirm("You sure nigga?")){
        setlogin_out('Loading...')
        await axios.post('/logout',{},{withCredentials: true})
        navigate(0)
    }
    }
   
    var defaults ={
        height: '5%',
        whiteSpace: 'nowrap', 
        overflow: 'hidden',
        width: '60%',
        alignSelf: 'end',
        transform: 'rotate(90deg)',
        justifySelf: 'right',
        gridRow: 1,
        justifyContent: 'center',
    }
    return(
        <header>
            <h1>TodoTheList</h1>
            <ul>
                <li>Expired</li>
                <li onClick={() =>{ 
                setongoingstyle({flex: '5'}) 
               setInterval(() => {
                setonexpired({...defaults, gridArea: '1/3/1/3' })
                setcompletedstyle({...defaults,  gridArea: '2/3/2/3'})
               }, 1000);
            
            }} >Ongoing tasks</li>
                <li>Done</li>
                <li style={{backgroundColor:"green"}} onClick={() => setpopup(true)}>Add item</li>
            </ul>
            <span className="Sign-Welcome">
                {currentuser?
            <p>What's on your mind today, <span>{String(currentuser).charAt(0).toUpperCase() + String(currentuser).slice(1)}</span>
            </p>: 'Loading'}
        </span>
    <button className="logout" onClick={handlelogout}>{login_out}</button>
        </header>
    )
}
