import { Fragment, useState } from 'react';
import './App.css';

function Todotable({items}){
return (
<div className='todobox'>
  <Todoitem item={items}></Todoitem>


  <button className='addbuttontodobox'>Add new Item</button>
</div>
)
}
function Todoitem(items){

return(
  <>
  <div className='todoitem'>
  <div className='leftofitem'>
  <div className='top'>{items.name}</div>
    <div className='description'>{items.desc}</div>
    <div className='end'>created at {items.time}</div>
  </div>
  <div className='rightofitem'>
    <button>Done</button>
  </div>
</div>
</>
)
}
function Additemform(){
 const [Name,setname] = useState('')
  const [desc,setdesc] = useState('')
  const [time,settime] = useState('')
  function handleadd(){
    setname('')
    setdesc('')
    settime('')
  }
  return (
    <div className="addingform">
    <input value={Name} onChange={(e) => setname(e.target.value)} placeholder='Item name'></input>
    <textarea value={desc} onChange={(e) => setdesc(e.target.value)} placeholder='description'></textarea>
    <input type='date' value={time} onChange={(e) => settime(e.target.value)}></input>
    <button onClick={handleadd}>Add</button>
  </div>
  )
}
function App() {
  return (
    <div className="App-header">
      <Todotable props />
      <Additemform/>
    </div>
  );
}

export default App;
