import { useEffect, useRef, useState } from "react";
import { Link, useNavigate } from 'react-router-dom';
import axios from "axios";
import "./App.css";
import Todotable from "./components/Todotable.js";
import Completed from "./components/completed.js";
import Additemform from "./components/Additemform.js";
import NavBar from "./components/NavBar.js";
import Expired from "./components/expired.js";
import { UContext } from "./index.js";
im
function App() {
  console.log('running')
  const navigate = useNavigate()
  const [currentuser, setcurrentuser] = useState('');
  useEffect(() => {
    axios.post('/currentUser').then((res) => {
                setcurrentuser(res.data)
    }
        )
        .catch((e) =>  navigate('/Sign'));
}, []);

  const storeditem = localStorage.getItem("items");
  const storedcompleted = localStorage.getItem("citems");
  const  [ongoingstyle, setongoingstyle] = useContext(UContext)
  const [popup, setpopup] = useState(false)
  const [Name, setname] = useState("");
  const [desc, setdesc] = useState("");
  const [time, settime] = useState("");
  const [items, setitems] = useState(storeditem ? JSON.parse(storeditem) : []);
  const [completedItems, setCompletedItems] = useState(
    storedcompleted ? JSON.parse(storedcompleted) : []
  );
const [expiredItems, setExpiredItems] = useState([])

  useEffect(
    () => localStorage.setItem("items", JSON.stringify(items)),
    [items]
  );
  useEffect(
    () => localStorage.setItem("citems", JSON.stringify(completedItems)),
    [completedItems]
  );
  function handledone(index) {
    // Move the completed item to the completedItems array
    let completedItem = items[index];
    completedItem.currentdate = new Date().toJSON().slice(0, 10);

    setCompletedItems([...completedItems, completedItem]);

    // Remove the completed item from the items array
    setitems(items.filter((_, i) => i !== index));
  }

  function handledelete(index) {
    const temp = completedItems.filter((item, i) => i !== index);

    setCompletedItems(temp);
  }


    function handledeleteex(index){
      const temp = expiredItems.filter((item, i) => i !== index);

      setExpiredItems(temp);
    }
  function handleadd() {
    setpopup(false)
    const currentdate = new Date().toJSON().slice(0, 10);

    // Trim and validate inputs
    let trimmedDesc = desc.trim();
    if (trimmedDesc === "") {
      trimmedDesc = "No Description";
    }

    if (Name.trim() === "" || time.trim() === "") {
      alert("Please fill in all fields.");
      return; // Exit if validation fails
    }

    const newItem = {
      Name: Name.trim(),
      desc: trimmedDesc,
      time: time.trim(),
      currentdate,
    };
    setitems((items) => [...items, newItem]);

    // Clear input fields
    setname("");
    setdesc("");
    settime("");
  }

  return (
    <>
    <div className="header">
            <NavBar  setpopup= {setpopup} username= {currentuser}></NavBar>
    </div>
    <div className="App-header">
      <Completed completedItems={completedItems} handledelete={handledelete} />
      <Expired handledeleteex={handledeleteex} expiredItems={expiredItems} />
      <Todotable items={items} handledone={handledone}/>

    </div>
    <Additemform 
      popup={popup}
      setpopup= {setpopup}
      Name={Name}
      desc={desc}
      time={time}
      setdesc={setdesc}
      setname={setname}
      settime={settime}
      handleadd={handleadd}
  />
      </>
  );
}

export default App;
