import { useContext, useEffect, useState } from "react";
import Todoitem from "./Todoitem.js";
import { UContext } from "../index.js";

export default function Todotable({ mode, items, handleit, setpopup }) {
  const [loadnow, setloadnow] = useState(true);
  const {
    ongoingstyle,
    setcompletedstyle,
    setongoingstyle,
    expiredstyle,
    completedstyle,
    setonexpired,
  } = useContext(UContext);
  
 
  var defaults = {
    flex: "0",
    overflow: "hidden",
  };

  if (mode === "current-task") {
    var expression = completedstyle.flex == 3 || expiredstyle.flex == 3;

    var textboxonclick = () => {
      setongoingstyle({ flex: "3" });
      setonexpired({ ...defaults });
      setcompletedstyle({ ...defaults });
    };
    var boxstyles = {
      justifyContent: items.length !== 0 ? "start" : "center",
      ...ongoingstyle,
    };
    var classnamefirstdiv = "todobox";
    var mappingexpression = !expression && loadnow;
    var h1style = expression
      ? {
          textWrap: "nowrap",
          transform: "rotate(-90deg)",
          top: "calc(50% - 25px)",
        }
      : { top: "-10px" };
  } else if (mode === "expired-task") {
    var expression = completedstyle.flex == 3 || ongoingstyle.flex == 3;

    var textboxonclick = () => {
      setongoingstyle({ ...defaults });
      setonexpired({ flex: "3" });
      setcompletedstyle({ ...defaults });
    };

    var classnamefirstdiv = "expiredbox todobox";
    var mappingexpression = !expression && loadnow;
    var boxstyles = {
      justifyContent: items.length !== 0 ? "start" : "center",
      ...expiredstyle,
    };

    if (ongoingstyle.flex == 3)
      h1style = {
        textWrap: "nowrap",
        transform: "rotate(90deg)",
        top: "calc(50% - 25px)",
      };
    else if (completedstyle.flex == 3)
      h1style = {
        textWrap: "nowrap",
        transform: "rotate(-90deg)",
        top: "calc(50% - 25px)",
      };
  } else if (mode === "completed-task") {
    var expression = ongoingstyle.flex == 3 || expiredstyle.flex == 3;

    var textboxonclick = () => {
      setongoingstyle({ ...defaults });
      setonexpired({ ...defaults });
      setcompletedstyle({ flex: "3" });
    };
    var boxstyles = {
      justifyContent: items.length !== 0 ? "start" : "center",
      ...completedstyle,
    };
    var classnamefirstdiv = "completedbox todobox";
    var mappingexpression = !expression && loadnow;
    var h1style = expression
      ? {
          textWrap: "nowrap",
          transform: "rotate(90deg)",
          top: "calc(50% - 25px)",
        }
      : { top: "-10px" };
  } 
  
  return (
    <div
      onClick={textboxonclick}
      className={classnamefirstdiv}
      style={boxstyles}
    >
      {mappingexpression &&
        items.map((item, index) => {
          return (
            <Todoitem
              items={item}
              handledone={() => handleit(index)}
              key={index}
            />
          );
        })}
      <h1 style={h1style} className="boxHeader">
        Current Tasks
      </h1>
      {items.length === 0 && !expression ? (
        <img
          alt="empty"
          className="Hidden"
          src="\flat-icon-trash-can-flies-260nw-2127192293.png"
          style={{ width: "100px" }}
        />
      ) : (
        ""
      )}
    </div>
  );
}
