import { Fragment, useRef, useState } from 'react';
import './App.css';

function Todotable({items,handledone}){
return (
<div className='todobox'>
{console.log(items)}
  {items.map((item,index) => {
    return <Todoitem items={item} handledone={handledone} key={index}/>
  })

  }
  <button className='addbuttontodobox'>Add new Item</button>
</div>
)
}
function Todoitem({items,handledone}){
return(
  <>
  <div className='todoitem'>
  <div className='leftofitem'>
  <div className='top'>{items.Name}</div>
    <div className='description'>{items.desc}</div>
    <div className='end'>created at {items.time}</div>
  </div>
  <div className='rightofitem'>
    <button>Done</button>
  </div>
</div>
</>
)
}
function Additemform({Name,desc,time,setname,setdesc,settime,handleadd}){

  return (
    <form className="addingform" onSubmit={(e) => { e.preventDefault(); handleadd();}}>
    <input value={Name} onChange={(e) => setname(e.target.value)} placeholder='Item name' required></input>
    <textarea value={desc} onChange={(e) => setdesc(e.target.value)} placeholder='description' required></textarea>
    <input type='date' value={time} onChange={(e) => settime(e.target.value)} required></input>
    <button >Add</button>
  </form>
  )

}
function Completed(){
  return(
    <div className='completedbox'>

    </div>
  )
}
function App() {
  const [Name,setname] = useState('')
  const [desc,setdesc] = useState('')
  const [time,settime] = useState('')
  const [items, setitems] = useState([])
  function handledone(){

  }
  function handleadd(){
    const newitem = {Name,desc,time}
    setitems([...items, newitem])
    setname('')
    setdesc('')
    settime('')
  }

  return (
    <div className="App-header">
      <Completed/>
      <Todotable items={items} handledone = {handledone}/>
      <Additemform Name={Name} desc={desc} time={time} setdesc={setdesc} setname={setname} settime={settime} handleadd={handleadd}/>
    </div>
  );
}

export default App;
