import { useContext, useEffect, useState } from "react";
import Todoitem from "./Todoitem.js";
import { UContext } from "../index.js";

export default function Todotable({mode, items, handledone, setpopup }) {
  const [loadnow,setloadnow] = useState(true)
  const  {ongoingstyle,setcompletedstyle, setongoingstyle, expiredstyle, completedstyle,setonexpired} = useContext(UContext)
  if(mode == 'current-task'){
    
  }
  else if(mode == 'expired-task'){
    alert('expired-task')
  }
  else if(mode == 'completed-task'){
    alert('completed-task')
  }
  else
  {alert('not detected')}



  var expression = (completedstyle.flex == 3 || expiredstyle.flex == 3) 
  useEffect(()=>{
    if(expression == false){
      setTimeout(() => {
        setloadnow(true)
      }, 500);
    }else{
      setloadnow(false)
    }
    
  }, [expression])

  var defaults ={
    flex: '0',
    overflow: 'hidden',
}
  return (

    <div
    onClick={() =>{ 
      setongoingstyle({flex: '3'}) 
      setonexpired({...defaults})
      setcompletedstyle({...defaults})

  }}
      className="todobox"
      style={{ justifyContent: items.length !== 0 ? "start" : "center", ...ongoingstyle}}
    >
      {(!expression && loadnow) && items.map((item, index) => {
        return (
          <Todoitem
            items={item}
            handledone={() => handledone(index)}
            key={index}
          />
        );
      })}
      <h1
        style={expression? {textWrap: "nowrap", transform: 'rotate(-90deg)', top:'calc(50% - 25px)'}: {top: '-10px'}}
        className="boxHeader"
      >
        Current Tasks
      </h1>
      {items.length === 0 && !expression? <img alt="empty" className="Hidden" src="\flat-icon-trash-can-flies-260nw-2127192293.png" style={{width: '100px'}}/> : ''}
    </div>
  );
}
