import { useEffect, useContext, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "./App.css";
import Todotable from "./components/Todotable.js";

import Additemform from "./components/Additemform.js";
import NavBar from "./components/NavBar.js";

import { UContext } from "./index.js";

function App() {
  const {setguest, completedstyle, ongoingstyle, expiredstyle, guest } = useContext(UContext);
  const navigate = useNavigate();
  const [currentuser, setcurrentuser] = useState("");






  const [popup, setpopup] = useState(false);
  const [Name, setname] = useState("");
  const [desc, setdesc] = useState("");
  const [time, settime] = useState("");
  const [items, setitems] = useState([]);
  const [completedItems, setCompletedItems] = useState([]);
  const [expiredItems, setExpiredItems] = useState([]);
  const Datenow = () => new Date().toJSON()

  useEffect(()=>{
    async function fetchdata(){
    if(guest)
    {
    var storeditem =JSON.parse(localStorage.getItem("items"))
    var storedcompleted = JSON.parse(localStorage.getItem("citems"))
    var storedeitems = JSON.parse(localStorage.getItem("eitems"))
    if(storeditem){
      setitems(storeditem)
    }
    if(storedcompleted){
      setCompletedItems(storedcompleted)
    }
    if(storedeitems){
      setExpiredItems(storedeitems)
    }
    }else{
      getitems()
    }
    async function getitems() {
      var storedeitems = await axios.post('/getsetitems',{itemtype: 'expired', mode: 'get'})
      var storeditem = await axios.post('/getsetitems',{itemtype: 'current', mode: 'get'})
      var storedcompleted = await axios.post('/getsetitems',{itemtype: 'completed', mode: 'get'})

    }
    if(storeditem){
      setitems(storeditem)
    }
    if(storedcompleted){
      setCompletedItems(storedcompleted)
    }
    if(storedeitems){
      setExpiredItems(storedeitems)
    }}
    fetchdata
    })


  useEffect(() => {
        if(guest){
          if(items.length != 0)
            localStorage.setItem("items", JSON.stringify(items))
        if(completedItems.length != 0)
          localStorage.setItem("citems", JSON.stringify(completedItems))
        if(expiredItems.length != 0)
          localStorage.setItem("eitems", JSON.stringify(expiredItems))
        }else{ //set items 
          if(items.length != 0)
          axios.post('/getsetitems',{itemtype: 'expired', item: expiredItems, mode: 'set'})
          if(completedItems.length != 0)
          axios.post('/getsetitems',{itemtype: 'completed', item: completedItems, mode: 'set'})
          if(expiredItems.length != 0)
          axios.post('/getsetitems',{itemtype: 'current', item: items, mode: 'set'})
        }
    }
    ,[items,completedItems,expiredItems]
  );

  useEffect(()=>{
    items.forEach((item , i) => {
      let k = new Date(item.time).getTime()
      let h = new Date().getTime()
      if(k <= h){
      let temp = [...expiredItems, items[i]]
      setExpiredItems(temp)
        handledelete(i, 'current-item')
      }
    });
  }, [items])


  function handledone(index) {
    // Move the completed item to the completedItems array
    let completedItem = items[index];
    completedItem.currentdate = Datenow();

    setCompletedItems([...completedItems, completedItem]);

    // Remove the completed item from the items array
    setitems(items.filter((_, i) => i !== index));
  }


  function handledelete(index, mode) {
    if(mode == "current-task"){const temp = items.filter((item, i) => i !== index);  setitems(temp);}
    if(mode == "expired-task"){const temp = expiredItems.filter((item, i) => i !== index);  setExpiredItems(temp);}
    if(mode == "completed-task")
   { const temp = completedItems.filter((item, i) => i !== index);  setCompletedItems(temp);}


  }
  function handleadd() {
    setpopup(false);
    let currentdate = Datenow();
    let hour = currentdate.split('T')[1].slice(0,5)
    currentdate = currentdate.slice(0,10)
    currentdate = currentdate + ' ' + hour
    // Trim and validate inputs
    let trimmedDesc = desc.trim();
    if (trimmedDesc === "") {
      trimmedDesc = "No Description";
    }

    if (Name.trim() === "" || time.trim() === "") {
      alert("Please fill in all fields.");
      return; // Exit if validation fails
    }

    const newItem = {
      Name: Name.trim(),
      desc: trimmedDesc,
      time: time.trim(),
      currentdate,
    };
    setitems((items) => [...items, newItem]);

    // Clear input fields
    setname("");
    setdesc("");
    settime("");
  }

  return (
    <>
      <div className="header">
        <NavBar setpopup={setpopup} username={currentuser}></NavBar>
      </div>
      <div className="App-header">
        <Todotable mode="current-task" items={items} handleit={handledone} />
        <Todotable
          mode="completed-task"
          items={completedItems}
          handleit={handledelete}
        />
        <Todotable
          mode="expired-task"
          items={expiredItems}
          handleit={handledelete}
        />

      </div>
      <Additemform
        popup={popup}
        setpopup={setpopup}
        Name={Name}
        desc={desc}
        time={time}
        setdesc={setdesc}
        setname={setname}
        settime={settime}
        handleadd={handleadd}
      />
    </>
  );
}

export default App;
