import { useEffect, useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./App.css";
import Todotable from "./components/Todotable.js";
import Additemform from "./components/Additemform.js";
import NavBar from "./components/NavBar.js";
import { UContext } from "./index.js";

function App() {
  const { setguest } = useContext(UContext);
  const navigate = useNavigate();
  const [currentuser, setcurrentuser] = useState("");

  useEffect(() => {
    axios
      .post("/currentUser")
      .then((res) => {
        setcurrentuser(res.data);
        setguest(false);
      })
      .catch((e) => setguest(true));
  }, []);

  const [popup, setpopup] = useState(false);
  const [Name, setname] = useState("");
  const [desc, setdesc] = useState("");
  const [time, settime] = useState("");
  const [items, setitems] = useState([]);
  const [completedItems, setCompletedItems] = useState([]);
  const [expiredItems, setExpiredItems] = useState([]);

  const Datenow = () => new Date().toJSON();

  // Load data from local storage on component mount
  useEffect(() => {
    const storeditem = JSON.parse(localStorage.getItem("items")) || [];
    const storedcompleted = JSON.parse(localStorage.getItem("citems")) || [];
    const storedeitems = JSON.parse(localStorage.getItem("eitems")) || [];

    setitems(storeditem);
    setCompletedItems(storedcompleted);
    setExpiredItems(storedeitems);
  }, []);

  // Save data to local storage when items change
  useEffect(() => {
    localStorage.setItem("items", JSON.stringify(items));
    localStorage.setItem("citems", JSON.stringify(completedItems));
    localStorage.setItem("eitems", JSON.stringify(expiredItems));
  }, [items, completedItems, expiredItems]);

  // Handle expired items
  useEffect(() => {
    const now = new Date().getTime();
    items.forEach((item, i) => {
      let itemTime = new Date(item.time).getTime();
      if (itemTime <= now) {
        setExpiredItems((prev) => [...prev, items[i]]);
        handledelete(i, "current-task");
      }
    });
  }, [items]);

  function handledone(index) {
    let completedItem = items[index];
    completedItem.currentdate = Datenow();
    setCompletedItems((prev) => [...prev, completedItem]);
    setitems((prev) => prev.filter((_, i) => i !== index));
  }

  function handledelete(index, mode) {
    if (mode === "current-task") {
      setitems((prev) => prev.filter((_, i) => i !== index));
    } else if (mode === "expired-task") {
      setExpiredItems((prev) => prev.filter((_, i) => i !== index));
    } else if (mode === "completed-task") {
      setCompletedItems((prev) => prev.filter((_, i) => i !== index));
    }
  }

  function handleadd() {
    setpopup(false);
    let currentdate = Datenow();
    let hour = currentdate.split('T')[1].slice(0, 5);
    currentdate = currentdate.slice(0, 10) + ' ' + hour;

    let trimmedDesc = desc.trim() === "" ? "No Description" : desc.trim();

    if (Name.trim() === "" || time.trim() === "") {
      alert("Please fill in all fields.");
      return;
    }

    const newItem = {
      Name: Name.trim(),
      desc: trimmedDesc,
      time: time.trim(),
      currentdate,
    };

    setitems((prev) => [...prev, newItem]);
    setname("");
    setdesc("");
    settime("");
  }

  return (
    <>
      <div className="header">
        <NavBar setpopup={setpopup} username={currentuser}></NavBar>
      </div>
      <div className="App-header">
        <Todotable mode="current-task" items={items} handleit={handledone} />
        <Todotable mode="completed-task" items={completedItems} handleit={handledelete} />
        <Todotable mode="expired-task" items={expiredItems} handleit={handledelete} />
      </div>
      <Additemform
        popup={popup}
        setpopup={setpopup}
        Name={Name}
        desc={desc}
        time={time}
        setdesc={setdesc}
        setname={setname}
        settime={settime}
        handleadd={handleadd}
      />
    </>
  );
}

export default App;
