import Todoitem from "./App.js";

export default function Todotable({ items, handledone, inputref }) {
  function focus() {
    inputref.current.focus();
    inputref.current.style.backgroundColor = "red";
    inputref.current.style.color = "yellow";
  }
  return (
    <div
      className="todobox"
      style={{ justifyContent: items.length !== 0 ? "start" : "center" }}
    >
      {items.map((item, index) => {
        return (
          <Todoitem
            items={item}
            handledone={() => handledone(index)}
            key={index}
          />
        );
      })}
      <button
        className="addbuttontodobox"
        onClick={focus}
        style={{
          position: items.length !== 0 ? "static" : "absolute",
          top: 15,
        }}
      >
        Add new Item
      </button>
      {items.length !== 0 ? "" : <p className="Hidden">No Tasks</p>}
    </div>
  );
}
