import { useContext } from "react";
import { UContext } from "../index.js";

export default function Completed({ completedItems, handledelete }) {
  const  {completedstyle, setcompletedstyle,ongoingstyle} = useContext(UContext)


    return (
      <div
        className="completedbox todobox"
        style={{
          justifyContent: completedItems.length !== 0 ? "start" : "center",
          ...completedstyle
        }}
      >
        {completedItems.map((item, index) => (
          <div key={index} className="todoitem completed">
            <div className="leftofitem">
              <div className="top">
                {item.Name.length > 20
                  ? item.Name.slice(0, 20) + "..."
                  : item.Name}{" "}
                {item.currentdate}
              </div>
              <hr></hr>
              <div className="description" style={{ textWrap: "pretty" }}>
                {item.desc.length > 50
                  ? item.desc.slice(0, 50) + "..."
                  : item.desc}
              </div>
              <hr></hr>
              <div className="end">
                Completed at {true && new Date().toJSON().slice(0, 10)}
              </div>
            </div>
            <div className="rightofitem">
              <button onClick={() => handledelete(index)}>Delete</button>
            </div>
          </div>
        ))}
         <h1
        className="boxHeader"
        style={Object.keys(ongoingstyle).length !== 0? {textWrap: "nowrap", transform: 'rotate(90deg)', top}: {top: '-10px'}}
      >
        Completed Tasks
      </h1>
      {completedItems.length === 0? (
      (Object.keys(completedstyle).length ==  0? <img 
        alt="empty" 
        className="Hidden" 
        src="/flat-icon-trash-can-flies-260nw-2127192293.png" 
        style={{ width: '100px' }} 
      />: null)
    ) : null}
      </div>
    );
  }
